    <h5>
        <?= ($bibleproject_video_count >= 5) ? '<i class="material-icons">playlist_play</i> Related Videos Playlist' : 'Related Videos' ?>
    </h5>
    <div class="row">
    <?
    if ($bibleproject_video_count <= 1)
    {
        // Individual embedded video display

        foreach ($search_results_books as $book)
        {
            if (is_array(BibleProject::$youtube_ids[$book]))
            {
                foreach(BibleProject::$youtube_ids[$book] as $youtube_id)
                {
                    ?>
                        <div class="col s12 m6 l<?= (12 / $bibleproject_video_count) ?>" style="margin-bottom: 2em;">
                            <div class="video-container">
                                <iframe src="https://www.youtube.com/embed/<?= $youtube_id ?>?rel=0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                            </div>
                        </div>
                    <?
                }
            }
            else
            {
                ?>
                <div class="col">
                    <div class="row center-align" >
                        <div style="width: 50%; margin: 0 auto 2em;">
                            <div class="video-container">
                                <iframe src="https://www.youtube.com/embed/<?= BibleProject::$youtube_ids[$book] ?>&rel=0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                            </div>
                        </div>
                    </div>
                </div>
                <?
            }
        }
        ?>
            </div>
        </div>
        <?
    }
    else
    {
        // Youtube playlist display

        foreach ($search_results_books as $book)
        {
            if (is_array(BibleProject::$youtube_ids[$book]))
            {
                foreach(BibleProject::$youtube_ids[$book] as $youtube_id)
                {
                    $youtube_ids[] = $youtube_id;
                }
            }
            else
            {
                $youtube_ids[] = BibleProject::$youtube_ids[$book];
            }
        }
        ?>
        <div class="row center-align" >
            <div style="margin: 0 auto 2em;">
                <div class="video-container">
                    <iframe src="https://www.youtube.com/embed?listType=playlist&playlist=<?= implode(',', $youtube_ids) ?>" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                </div>
            </div>
        </div>
        <?
    }
